import './index.scss';
import axios from 'axios'

import Cabecalho from '../../components/cabecalho';

import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
    const [email, setEmail] = useState('')
    const [senha, setSenha] = useState('')
    const [showLogin, setShowLogin] = useState(false)

    const navigate = useNavigate();

    async function logar() {
        try {
            let body = {
                "email": email,
                "senha": senha
            }

            let resp = await axios.post('http://localhost:3010/login', body);

            localStorage.setItem('TOKEN', resp.data.token);

            navigate('/home');
        }
        catch (err) {
            alert(err.response.data.erro)
        }
    }

    function toggleLogin() {
        setShowLogin(!showLogin)
    }

    return (
        <div className='pagina-ln-login'>
            <Cabecalho onLoginClick={toggleLogin} showLogin={showLogin} />
            <h1> "Conteúdo dev, em um só lugar" </h1>

            <div className='icons'>
                <img src='./img/Fundo.png'></img>
                <img src='./img/azure.png'></img>
                <img src='./img/mysql.png'></img>
                <img src='./img/netifly.png'></img>
                <img src='./img/node.png'></img>
                <img src='./img/prompt.png'></img>
                <img src='./img/react.png'></img>
                <img src='./img/sass.png'></img>
                <img src='./img/visual.png'></img>
            </div>

            {showLogin && (
                <div className='form'>
                    <div>
                        <h1>DEV<p>HUB</p></h1>
                        <label>E-mail</label>
                        <input type='text' value={email} onChange={e => setEmail(e.target.value)} />
                        <img src='./img/usuario.png'></img>
                    </div>
                    <div>
                        <label>Senha</label>
                        <input type='password' value={senha} onChange={e => setSenha(e.target.value)} />
                        <img src='./img/senha.png'></img>
                    </div>
                    <button onClick={logar} className='login-button'>
                        Entrar
                    </button>
                </div>
            )}
        </div>
    )
}
